import React from "react";
import { useExpenseContext } from "../context/ExpenseContext";

const TransactionList = () => {
  const { transactions, deleteTransaction } = useExpenseContext();

  const handleDeleteTransaction = (id) => {
    // تایید حذف تراکنش
    if (window.confirm("آیا مطمئن هستید که می‌خواهید این تراکنش را حذف کنید؟")) {
      deleteTransaction(id);
    }
  };

  return (
    <div>
      <h3 className="text-xl font-bold mb-4">لیست تراکنش‌ها</h3>
      {/* اگر لیست تراکنش‌ها خالی است، پیامی نشان دهیم */}
      {transactions.length === 0 ? (
        <p>تراکنشی برای نمایش وجود ندارد.</p>
      ) : (
        <ul>
          {transactions.map((transaction) => (
            <li key={transaction.id} className="flex justify-between items-center mb-4 p-2 border-b">
              <div>
                <span className="font-semibold">{transaction.title}</span>
                <span className="text-gray-500"> - {transaction.category}</span>
                <span className="text-gray-400"> - {new Date(transaction.date).toLocaleDateString()}</span>
              </div>
              <div className="flex items-center">
                <span>{transaction.amount} تومان</span>
                <button
                  className="ml-4 text-red-500 hover:text-red-700"
                  onClick={() => handleDeleteTransaction(transaction.id)}
                >
                  حذف
                </button>
              </div>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default TransactionList;
