import React, { useState } from "react";
import { useExpenseContext } from "../context/ExpenseContext";

const AddTransaction = () => {
  const { addTransaction, categories } = useExpenseContext();
  const [title, setTitle] = useState("");
  const [amount, setAmount] = useState("");
  const [date, setDate] = useState("");
  const [category, setCategory] = useState("");
  const [type, setType] = useState("expense");
  const [error, setError] = useState("");  // اضافه کردن وضعیت برای خطا

  const handleSubmit = (e) => {
    e.preventDefault();
    
    // بررسی خالی بودن فیلدها
    if (!title || !amount || !date || !category) {
      setError("لطفاً همه فیلدها را پر کنید!");
      return;
    }

    // بررسی مبلغ منفی یا صفر
    if (parseFloat(amount) <= 0) {
      setError("مبلغ باید بزرگتر از صفر باشد!");
      return;
    }

    // ارسال تراکنش
    addTransaction({
      id: Date.now(),
      title,
      amount: parseFloat(amount),
      date,
      category,
      type,
    });

    // ریست کردن فیلدها
    setTitle("");
    setAmount("");
    setDate("");
    setCategory("");
    setError("");  // پاک کردن پیام خطا بعد از موفقیت

  };

  return (
    <form onSubmit={handleSubmit} className="space-y-4 p-4 max-w-lg mx-auto">
      {error && <div className="text-red-500">{error}</div>} {/* نمایش پیام خطا */}

      <input
        type="text"
        placeholder="عنوان"
        value={title}
        onChange={(e) => setTitle(e.target.value)}
        className="w-full p-2 border rounded"
      />
      <input
        type="number"
        placeholder="مبلغ"
        value={amount}
        onChange={(e) => setAmount(e.target.value)}
        className="w-full p-2 border rounded"
      />
      <input
        type="date"
        value={date}
        onChange={(e) => setDate(e.target.value)}
        className="w-full p-2 border rounded"
      />
      <select
        value={category}
        onChange={(e) => setCategory(e.target.value)}
        className="w-full p-2 border rounded"
      >
        <option value="">انتخاب دسته‌بندی</option>
        {categories.map((cat, index) => (
          <option key={index} value={cat}>
            {cat}
          </option>
        ))}
      </select>
      <select
        value={type}
        onChange={(e) => setType(e.target.value)}
        className="w-full p-2 border rounded"
      >
        <option value="expense">هزینه</option>
        <option value="income">درآمد</option>
      </select>
      <button
        type="submit"
        className="w-full p-2 bg-blue-500 text-white rounded"
      >
        افزودن تراکنش
      </button>
    </form>
  );
};

export default AddTransaction;
