import React, { useState } from "react";
import { useExpenseContext } from "../context/ExpenseContext";

const Reminder = () => {
  const { addReminder } = useExpenseContext();
  const [reminder, setReminder] = useState("");
  const [reminderDate, setReminderDate] = useState("");
  const [error, setError] = useState("");

  const handleAddReminder = () => {
    // بررسی فیلدهای خالی
    if (!reminder || !reminderDate) {
      setError("لطفاً تمام فیلدها را پر کنید.");
      return;
    }

    // بررسی تاریخ آینده
    const currentDate = new Date();
    const selectedDate = new Date(reminderDate);
    if (selectedDate < currentDate) {
      setError("تاریخ باید بعد از امروز باشد.");
      return;
    }

    // افزودن یادآوری
    const newReminder = { reminder, reminderDate };
    addReminder(newReminder);
    setReminder("");
    setReminderDate("");
    setError(""); // پاک کردن خطا بعد از موفقیت
  };

  return (
    <div>
      <h3 className="text-xl font-semibold mb-4">یادآوری‌ها</h3>

      {/* نمایش پیام خطا */}
      {error && <div className="text-red-500 mb-2">{error}</div>}

      <input
        type="text"
        placeholder="یادآوری"
        value={reminder}
        onChange={(e) => setReminder(e.target.value)}
        className="w-full p-2 border rounded mb-2"
      />
      <input
        type="date"
        value={reminderDate}
        onChange={(e) => setReminderDate(e.target.value)}
        className="w-full p-2 border rounded mb-2"
      />
      <button
        type="button"
        onClick={handleAddReminder}
        className="w-full p-2 bg-green-500 text-white rounded"
      >
        افزودن یادآوری
      </button>
    </div>
  );
};

export default Reminder;
