import React, { useState } from "react";
import { useExpenseContext } from "../context/ExpenseContext";

const AddTransaction = () => {
  const { addTransaction, categories, addCategory } = useExpenseContext();
  const [title, setTitle] = useState("");
  const [amount, setAmount] = useState("");
  const [date, setDate] = useState("");
  const [category, setCategory] = useState("");
  const [newCategory, setNewCategory] = useState("");  // برای دسته‌بندی سفارشی
  const [type, setType] = useState("expense");

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!title || !amount || !date || (!category && !newCategory)) return;

    addTransaction({
      id: Date.now(),
      title,
      amount: parseFloat(amount),
      date,
      category: newCategory || category,
      type
    });

    if (newCategory) {
      addCategory(newCategory);  // افزودن دسته‌بندی سفارشی
    }

    setTitle("");
    setAmount("");
    setDate("");
    setCategory("");
    setNewCategory("");
  };

  return (
    <form onSubmit={handleSubmit} className="space-y-4 p-4 max-w-lg mx-auto">
      <input type="text" placeholder="عنوان" value={title} onChange={(e) => setTitle(e.target.value)} className="w-full p-2 border rounded" />
      <input type="number" placeholder="مبلغ" value={amount} onChange={(e) => setAmount(e.target.value)} className="w-full p-2 border rounded" />
      <input type="date" value={date} onChange={(e) => setDate(e.target.value)} className="w-full p-2 border rounded" />
      
      <select value={category} onChange={(e) => setCategory(e.target.value)} className="w-full p-2 border rounded">
        <option value="">انتخاب دسته‌بندی</option>
        {categories.map((cat, index) => (
          <option key={index} value={cat}>{cat}</option>
        ))}
      </select>

      <input
        type="text"
        placeholder="دسته‌بندی جدید"
        value={newCategory}
        onChange={(e) => setNewCategory(e.target.value)}
        className="w-full p-2 border rounded mb-2"
      />

      <select value={type} onChange={(e) => setType(e.target.value)} className="w-full p-2 border rounded">
        <option value="expense">هزینه</option>
        <option value="income">درآمد</option>
      </select>

      <button type="submit" className="w-full p-2 bg-blue-500 text-white rounded">افزودن تراکنش</button>
    </form>
  );
};

export default AddTransaction;
