import React, { useState } from "react";
import { useExpenseContext } from "../context/ExpenseContext";

const TransactionList = () => {
  const { transactions, deleteTransaction } = useExpenseContext();
  const [searchTerm, setSearchTerm] = useState("");
  const [categoryFilter, setCategoryFilter] = useState("");

  // فیلتر کردن تراکنش‌ها بر اساس جستجو و دسته‌بندی
  const filteredTransactions = transactions.filter(transaction => 
    (transaction.title.includes(searchTerm) || transaction.amount.toString().includes(searchTerm)) && 
    (categoryFilter ? transaction.category === categoryFilter : true)
  );

  return (
    <div>
      <h3 className="text-xl font-bold mb-4">لیست تراکنش‌ها</h3>
      {/* فیلد جستجو */}
      <input
        type="text"
        placeholder="جستجو بر اساس عنوان یا مبلغ"
        value={searchTerm}
        onChange={(e) => setSearchTerm(e.target.value)}
        className="w-full p-2 border rounded mb-2"
      />
      {/* فیلتر دسته‌بندی */}
      <select
        value={categoryFilter}
        onChange={(e) => setCategoryFilter(e.target.value)}
        className="w-full p-2 border rounded mb-2"
      >
        <option value="">انتخاب دسته‌بندی</option>
        <option value="خوراک">خوراک</option>
        <option value="حمل و نقل">حمل و نقل</option>
        <option value="قبوض">قبوض</option>
        <option value="تفریح">تفریح</option>
      </select>

      <ul>
        {filteredTransactions.map((transaction) => (
          <li key={transaction.id} className="flex justify-between items-center">
            <div>
              <span className="font-semibold">{transaction.title}</span>
              <span className="text-gray-500"> - {transaction.category}</span>
            </div>
            <div className="flex items-center">
              <span>{transaction.amount} تومان</span> {/* افزودن واحد پول */}
              <button
                className="ml-4 text-red-500"
                onClick={() => deleteTransaction(transaction.id)}
              >
                حذف
              </button>
            </div>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default TransactionList;
