import React from "react";
import { useExpenseContext } from "../context/ExpenseContext";

const AdminDashboard = () => {
  const { transactions, wallets, reminders } = useExpenseContext();

  // محاسبه مجموع درآمد و هزینه‌ها
  const totalIncome = transactions
    .filter(transaction => transaction.type === "income")
    .reduce((acc, curr) => acc + curr.amount, 0);

  const totalExpense = transactions
    .filter(transaction => transaction.type === "expense")
    .reduce((acc, curr) => acc + curr.amount, 0);

  // محاسبه مجموع موجودی کیف پول‌ها
  const totalWalletBalance = wallets.reduce((acc, wallet) => acc + wallet.balance, 0);

  // تاریخ آخرین تراکنش
  const latestTransactionDate = transactions.length
    ? new Date(Math.max(...transactions.map(transaction => new Date(transaction.date).getTime()))).toLocaleDateString()
    : "هیچ تراکنشی ثبت نشده";

  // تاریخ آخرین یادآوری
  const latestReminderDate = reminders.length
    ? new Date(Math.max(...reminders.map(reminder => new Date(reminder.reminderDate).getTime()))).toLocaleDateString()
    : "هیچ یادآوری ثبت نشده";

  return (
    <div className="p-4">
      <h3 className="text-xl font-bold mb-4">داشبورد مدیریت</h3>
      <div>
        <h4 className="text-lg mb-2">تعداد تراکنش‌ها: {transactions.length}</h4>
        <h4 className="text-lg mb-2">تعداد کیف پول‌ها: {wallets.length}</h4>
        <h4 className="text-lg mb-2">تعداد یادآوری‌ها: {reminders.length}</h4>

        {/* مجموع درآمد و هزینه‌ها */}
        <h4 className="text-lg mb-2">مجموع درآمد: {totalIncome} تومان</h4>
        <h4 className="text-lg mb-2">مجموع هزینه‌ها: {totalExpense} تومان</h4>

        {/* مجموع موجودی کیف پول‌ها */}
        <h4 className="text-lg mb-2">مجموع موجودی کیف پول‌ها: {totalWalletBalance} تومان</h4>

        {/* تاریخ آخرین تراکنش و یادآوری */}
        <h4 className="text-lg mb-2">آخرین تراکنش: {latestTransactionDate}</h4>
        <h4 className="text-lg mb-2">آخرین یادآوری: {latestReminderDate}</h4>
      </div>
    </div>
  );
};

export default AdminDashboard;
