import React from "react";
import { Pie, Line } from "react-chartjs-2";
import { useExpenseContext } from "../context/ExpenseContext";
import Chart from "chart.js/auto";

const ExpenseChart = () => {
  const { transactions } = useExpenseContext();

  const pieData = {
    labels: ["خوراک", "حمل و نقل", "قبوض", "تفریح"],
    datasets: [
      {
        data: transactions.reduce((acc, curr) => {
          const categoryIndex = ["خوراک", "حمل و نقل", "قبوض", "تفریح"].indexOf(curr.category);
          if (categoryIndex !== -1) acc[categoryIndex] += curr.amount;
          return acc;
        }, [0, 0, 0, 0]),
        backgroundColor: ["#FF6384", "#36A2EB", "#FFCE56", "#4BC0C0"],
      }
    ]
  };

  const lineData = {
    labels: transactions.map(transaction => transaction.date),
    datasets: [
      {
        label: "هزینه ها",
        data: transactions.filter(transaction => transaction.type === "expense").map(transaction => transaction.amount),
        borderColor: "red",
        fill: false
      },
      {
        label: "درآمد ها",
        data: transactions.filter(transaction => transaction.type === "income").map(transaction => transaction.amount),
        borderColor: "green",
        fill: false
      }
    ]
  };

  return (
    <div>
      <h3 className="text-xl font-semibold mb-4">گزارش‌ها</h3>
      <div className="grid grid-cols-2 gap-4">
        <div>
          <h4 className="text-lg mb-2">نمودار دایره‌ای هزینه‌ها</h4>
          <Pie data={pieData} />
        </div>
        <div>
          <h4 className="text-lg mb-2">نمودار خطی روند هزینه و درآمد</h4>
          <Line data={lineData} />
        </div>
      </div>
    </div>
  );
};

export default ExpenseChart;
