import React from "react";
import { useExpenseContext } from "../context/ExpenseContext";

const TransactionList = () => {
  const { transactions, deleteTransaction } = useExpenseContext();

  return (
    <div>
      <h3 className="text-xl font-bold mb-4">لیست تراکنش‌ها</h3>
      <ul>
        {transactions.map((transaction) => (
          <li key={transaction.id} className="flex justify-between items-center">
            <div>
              <span className="font-semibold">{transaction.title}</span>
              <span className="text-gray-500"> - {transaction.category}</span>
            </div>
            <div className="flex items-center">
              <span>{transaction.amount} تومان</span>
              <button
                className="ml-4 text-red-500"
                onClick={() => deleteTransaction(transaction.id)}
              >
                حذف
              </button>
            </div>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default TransactionList;
