import React from "react";
import { useExpenseContext } from "../context/ExpenseContext";

const AdminDashboard = () => {
  const { transactions = [], wallets = [], reminders = [] } = useExpenseContext();

  // محاسبه مجموع درآمدها و هزینه‌ها
  const totalIncome = transactions
    .filter(transaction => transaction.type === "income")
    .reduce((sum, transaction) => sum + transaction.amount, 0);

  const totalExpense = transactions
    .filter(transaction => transaction.type === "expense")
    .reduce((sum, transaction) => sum + transaction.amount, 0);

  return (
    <div className="p-6 bg-white shadow-lg rounded-lg max-w-3xl mx-auto">
      <h3 className="text-2xl font-semibold text-gray-800 mb-6 text-center">داشبورد مدیریت</h3>
      <div className="grid grid-cols-1 sm:grid-cols-2 gap-6">
        {/* کارت تعداد تراکنش‌ها */}
        <div className="p-4 bg-blue-100 rounded-lg shadow-md">
          <h4 className="text-lg font-semibold text-gray-700 mb-2">تعداد تراکنش‌ها</h4>
          <p className="text-2xl font-bold text-gray-800">{transactions.length}</p>
        </div>

        {/* کارت تعداد کیف پول‌ها */}
        <div className="p-4 bg-indigo-100 rounded-lg shadow-md">
          <h4 className="text-lg font-semibold text-gray-700 mb-2">تعداد کیف پول‌ها</h4>
          <p className="text-2xl font-bold text-gray-800">{wallets.length}</p>
        </div>

        {/* کارت تعداد یادآوری‌ها */}
        <div className="p-4 bg-yellow-100 rounded-lg shadow-md">
          <h4 className="text-lg font-semibold text-gray-700 mb-2">تعداد یادآوری‌ها</h4>
          <p className="text-2xl font-bold text-gray-800">{reminders.length}</p>
        </div>

        {/* کارت مجموع درآمدها */}
        <div className="p-4 bg-green-100 rounded-lg shadow-md">
          <h4 className="text-lg font-semibold text-gray-700 mb-2">مجموع درآمدها</h4>
          <p className="text-2xl font-bold text-green-600">{totalIncome} تومان</p>
        </div>

        {/* کارت مجموع هزینه‌ها */}
        <div className="p-4 bg-red-100 rounded-lg shadow-md">
          <h4 className="text-lg font-semibold text-gray-700 mb-2">مجموع هزینه‌ها</h4>
          <p className="text-2xl font-bold text-red-600">{totalExpense} تومان</p>
        </div>
      </div>
    </div>
  );
};

export default AdminDashboard;
